INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('Super NES Classic', 'The Super NES Classic Edition system looks and feels just like the original 90s home console, except it’s super small. Two wired Super NES Classic Controllers are included for instant multiplayer action. Just plug it in and play!', 119.95, 1);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('Sega Genesis', 'The iconic SEGA Genesis console that defined a generation of gaming returns in a slick, miniaturized unit. Included are two wired controllers.', 69.99, 2);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('NES Classic', 'The NES Classic Edition system is a miniaturized version of the groundbreaking NES, originally released in 1985. Just plug the NES Classic Edition into your TV, pick up that gray controller, and rediscover the joy of NES games. Included is one NES Classic Controller.', 59.99, 1);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('Playstation Classic', 'A miniature recreation of the original PlayStation. The design of PlayStation Classic perfectly mirrors the original console, with the same logo, button layout and packaging – but 45% smaller than 1994’s PlayStation. This miniature console comes  with an HDMI cable to connect to your TV complete with two wired controllers, plus a virtual memory card to save your adventures as you switch between titles.', 99.99, 3);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('Commodore 64', 'The World’s Best-Selling Home Computer – Re-reborn. The C64 is back, this time full-sized with a working keyboard for the dedicated retro home-computer fan. Included along with the C64 Home Computer is a full-size working keyboard and a micro-switch USB joystick.', 88.99, 4);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('Game Boy', 'Everyone loves Game Boy. Packing a huge amount of power into a tiny package, this little console proved a revolution in videogaming when it launched in Japan in 1989. Since then, this pocket-sized system has sold over 100 million units, giving gamers all over the world the freedom to play their favourite games no matter where they are.', 59.95, 1);
INSERT INTO console_console (name, description, price, manufacturer_id) VALUES('ZX Spectrum', 'One of the most popular home computers ever made. ', 68.95, 5);
