INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Super Mario World', 'Super Mario World is a 1990 side-scrolling platform game developed and published by Nintendo for the Super Nintendo Entertainment System. The story follows Marios quest to save Princess Toadstool and Dinosaur Land from the series antagonist Bowser and his minions, the Koopalings.', 15.99, 1990, 'Nintendo', 1, [1]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Donkey Kong Country', 'Donkey Kong Country is a platforming game in which the player guides protagonists Donkey Kong (DK) and Diddy Kong (Diddy) through 40 side-scrolling levels. DK and Diddy are the games main characters and the only playable characters. The player can switch between Kongs if both characters are present.', 18.99, 1994, 'Nintendo', 1, [1]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Sonic the Hedgehog', 'Sonic is a blue anthropomorphic hedgehog who can run at supersonic speeds and curl into a ball, primarily to attack enemies. Sonic must race through levels, collecting power-up rings and avoiding obstacles and enemies.', 9.49, 1991, 'Sega', 2, 1);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Sonic the Hedgehog 2', 'Sonic the Hedgehog 2 is a 2D side-scrolling platform game. The game stars Sonic the Hedgehog and his sidekick, Miles "Tails" Prower, who is described as having idolized Sonic as a child and wanting to keep up with him. At the games start, the player can select to either play as Sonic, Tails, or both.', 9.49, 1992, 'Sega', 2, [1]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Pac-Man', 'Pac-Man is a maze chase video game. The player controls the titular character through an enclosed maze; the objective of the game is to eat all of the dots placed in the maze while avoiding four colored ghosts — Blinky (red), Pinky (pink), Inky (cyan), and Clyde (orange) — that pursue him.', 12.49, 1980, 'Namco', 3, [2]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Super Mario Bros. 3', 'The plot of Super Mario Bros. 3 is described in the instruction booklet. The Mushroom World, the setting of the game, is invaded by the Koopalings, Bowsers seven children. The Koopalings conquer each of the seven kingdoms by stealing its kings magical wand and using it to transform him into an animal.', 11.99, 1988, 'Nintendo', 3, [1]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('R4: Ridge Racer Type 4', 'R4: Ridge Racer Type 4 is an arcade-style racing game with a strong emphasis on powersliding around corners. Vehicles use one of two styles of powersliding depending on the make of car they select: Drift and Grip. ... Vehicles are earned by playing through the games Grand Prix mode.', 29.49, 1998, 'Namco', 4, [3]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Crash Bandicoot', 'Crash is a mutant eastern barred bandicoot who was genetically enhanced by the series main antagonist Doctor Neo Cortex and soon escaped from Cortexs castle after a failed experiment in the "Cortex Vortex". The story follows Crash as he aims to prevent Brio and Cortexs plans for world domination, and rescue his girlfriend Tawna, a female bandicoot also evolved by Brio and Cortex.', 34.95, 1996, 'Naughty Dog', 4, [1,3]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Impossible Mission', 'The player takes the role of a secret agent who must stop an evil genius, Professor Elvin Atombender, who is believed to be tampering with national security computers. The player races against the clock to reassemble and decrypt the password to Atombenders control room while avoiding deadly robots.', 24.99, 1984, 'Epyx', 5, [1,4]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('California Games', 'Branching from their popular Summer Games and Winter Games series, this game consists of a collection of outdoor sports purportedly popular in California. Sports include Half-Pipe, Roller-skating and surfing among others.', 26.99, 1987, 'Epyx', 5, [5]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Tetris', 'The game has a simple goal of destroying lines of block before it reaches the top. The line is made up of a square block. Tetrominoes is the shape of the 4 connected blocks that falls vertically down.', 12.49, 1989, 'Nintendo', 6, [6]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Kirbys Dream Land', 'Kirby and Gooey are on a fishing trip, when all of a sudden, Dark Matter appears and shatters the planets rings. He then proceeds to possess various Dream-Landers, including King Dedede. Kirby and Gooey then team up with Rick, Coo, and Kine, as well as new characters Pitch, ChuChu, and Nago.', 14.95, 1992, 'HAL Laboratory', 6, [1,7]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Manic Miner', 'The first game in the Miner Willy series and among the early titles in the platform game genre. In each of the twenty caverns, each one screen in size, are several flashing objects, which the player must collect before Willys oxygen supply runs out.', 22.39, 1983, 'Matthew Smith', 7, [1]);
INSERT INTO game_game (name, description, price, release_year, developer, console_id, genre_id) VALUES('Jet Set Willy', 'Jet Set Willy is a flip-screen platform game in which the player moves the protagonist, Willy, from room to room in his mansion collecting objects. Unlike the screen-by-screen style of its prequel, the player can explore the mansion at will. Willy is controlled using only left, right and jump.', 25.99, 1984, 'Matthew Smith', 7, [1]);
